import { useState } from "react";
import './TestStateFunc.css'

const TestStateFunc = () => {
    const [car, setCar] = useState('Ford')

    const whichCar=(event)=>{
        event.target.closest('h1').className.toggle('white-Text')
        if(car==='Tesla'){
            return setCar('Mustang')
        }else{
            return setCar('Tesla')
        }

    }


    return (
        <>
            <h1 value=''>my favorite car is a {car}</h1>
            <button onClick={whichCar}>click me</button>
        </>

    )
}

export default TestStateFunc
