import React, {useEffect, useRef} from 'react';

function Test(props) {
    const inputRef = useRef(null)
    useEffect(()=>{
        //focus the input element
        inputRef.current.focus()
    }, [])
    const onSubmitStuff=()=>{
        console.log(inputRef);
        props.setName(inputRef)
        inputRef.current.value = ''
    }
    return ( <div>
        <input type='text' ref={inputRef} placeholder={`hey ${props.name? props.name :'user'} type here!`}></input>
        <button onClick={onSubmitStuff}>click me !</button>
    </div> );
}

export default Test;
