import React, { useEffect, useState } from 'react'
import Button from '../UI/Button/Button'

const FetchingOne = () => {
    const [oneGundam, setGundam] = useState('')
    const [btnTrigger, setBtnTrigger] = useState(false)
    const [name, setName] = useState('')
    const [idApi, setIDForApi] = useState('')
    const [id, setGId] = ('')

    useEffect(()=>{
        if (btnTrigger) {
            const res = fetch(`http://localhost:4000/gundams/${idApi}`)
            res.then(res => res.json())
                .then(result => {
                    console.log(result);
                    setGundam(result.gundam)
                    setName(result.pilot)
                })
        } else {
            setGundam('')
            setName('')
        }
    }, [btnTrigger, idApi])
    const buttonHandler = ()=>{
        setBtnTrigger(!btnTrigger)
        setIDForApi(id)
        setId('')
    }


    return (
        <div>
            <label>
                Which Gundam are you looking for:
                <input type="number" value={id} onChange={(e) => { setGId(e.target.value)}}/>
            </label>
            <Button onClick={buttonHandler}>Submit your Gundam </Button>
            <h3>{`${name} : ${oneGundam}`}</h3>
        </div>
    )
}

export default FetchingOne
