    const submitHandler = (event) => {
        event.preventDefault()
        console.log('from test', userInput);
        const newObj = {
            uInput: userInput
        }
        props.checkTest(newObj)
        user('')
    }

    return (
        <>
            <div>
                <label>Test</label>
                <input type='text' ref={userInput}></input>
                <button onClick={submitHandler}>LiftState</button>
            </div>
            <h2>{nameCtx.name}</h2>
        </>

    )
}
