import React, {useState} from "react";
import ComponentA from './components/contextChange/ComponentA'
import ComponentD from './components/contextStatic/ComponentD'
import './index.css'
import ChangeContext from "./store/context-change";


function App() {
 const [nameChange, setNameChange ]= useState('')

 const onChangeNameHandler = (event)=>{
   setNameChange(event.target.value)
 }
 const onSubmit ={}

  return (
    <div className="App">
      <h1>Welcome</h1>
      <ChangeContext.Provider value={{name:'Wallie from change context'}}>
      <ComponentA></ComponentA>
      </ChangeContext.Provider>
      <ComponentD></ComponentD>
      <form onSubmit={onSubmit}>
        <label>Change the state and Context!</label>
        <input type='text' value={nameChange} onChange={onChangeNameHandler}></input>    
      <button type="submit"></button>
      </form>
    </div>
  );
}


export default App;
