import React, {useState, useEffect, useRef} from "react";

const ControlledFStateAndRef = (props)=>{
    const [userInput, setUserInput] = useState('')
    const inputRef = useRef()
    useEffect(()=>{
        inputRef.current.focus()
        console.log('destructured properly');
    },[])

    const handleChange = event =>{
        setUserInput(event.target.value)
    }

    const handleSubmit = ()=>{
        console.log(userInput , ' from functional controlled Component');
        props.setName(userInput)
        setUserInput('')
    }
    return (
        <>
            <input
                type='text'
                ref={inputRef}
                value={userInput}
                onChange={handleChange}
            />
            <button onClick={handleSubmit}>Click me Controlled Functional</button>
        </>
    )
}

export default ControlledFStateAndRef
