import React, { useState } from 'react'

const CounterComp = () => {
    const [counter, setCounter] = useState(0)

    document.title = counter 
    return (
        <div>
            <p>{counter}</p>
            <button onClick={()=>setCounter(counter + 1)}>the counter is {counter}</button>
        </div>
    )
}

export default CounterComp
