import React, { useEffect,useState } from 'react'

const CounterComp = () => {
    const [counter, setCounter] = useState({isValid:null, count:0})






    return (
        <div>
           <p>{counter.count}</p>
            <button onClick={()=>setCounter((prev)=>{prev.count + 1})}>the counter is {counter}</button>
        </div>
    )
}

export default CounterComp
