import React, { useReducer } from 'react';

const makeFullName = (state, action)=>{
    switch(action.type){
        case 'firstName':
            return {...state, firstName : action.firstName}
        case 'lastName':
            return {...state, lastName : action.lastName}
        case 'fullName':
            return { ...state, fullName: state.firstName + '' + state.lastName}
        default:
            return state;
    }
}


function FullNameState() {

    const [fullState , dispatch] = useReducer(makeFullName, {
        firstName:'', 
        lastName:'', 
        fullName:firstName + ''
    })

    const getFirstName = event =>{
        event.preventDefault()
        const first = event.target.value
        dispatch({type: 'firstName', first})
        console.log(first, ': from getFirstName')
    }
    const getLastName = event =>{
        event.preventDefault()
        const last = event.target.value
        dispatch({ type: 'lastName', last })
        console.log(last, ': from getLastName')
    }



    const ShowFullN = (e) => {
        e.preventDefault()
        // setFullName(firstName + " " + lastName)
        dispatch({type: 'fullName'})
        console.log(fullState.fullName);
    }

    return (
        <div>
            <form onSubmit={ShowFullN}>
                <label htmlFor='fName'>First Name :
                    < input id='fName' type='text'  onChange={getFirstName} />
                </label>
                <label htmlFor='lName'>Last Name :
                    < input id='lName' type='text'  onChange={getLastName} />
                </label>
                <button type='submit'> print fullName</button>
            </form>
            <div>
                <h1>{fullState.fullName}</h1>
            </div>
        </div>
    );
}

export default FullNameState;
