import React, { useRef, useState } from 'react';

function CountOneRef() {
    // takes 2 params first one being the reducer function 
    // the second one being the initial state 
    const refCount = useRef(0)// these arent defined yet 
    // const [num, setNum] = useState(0)
    return (
        <div>
            <div ref={refCount}>Count - {refCount.current.v} With state</div>
            <button onClick={() => {refCount.current.value += 1
            console.log(refCount.current.value)}}>Increment</button>
            <button onClick={() => refCount.current.value -= 1}>Decrement</button>
            <button onClick={() => refCount.current.value = 0}>Reset</button>
        </div>
    );
}

export default CountOneRef;
