    }

    handleSubmit = (e) => {
        // const name = this.userValue.value;
        console.log(this.state.text);
    }
    render() {
        return (
            <>
                <input
                    type='text'
                    value={this.state.text}
                    onChange={this.handleChange}
                />
                <button onClick={this.handleSubmit}>Click me UnControlled</button>
            </>
        )
    }
}

export default Controlled;
