import React, { useState } from 'react';
import './App.css';

function App() {
  const [showParagraph, setShowParagraph] = useState(true)
  return (
    <div className="app">
      <h1>Hi there!</h1>
      {showParagraph && <p>This is new!</p>}
      <Button onClick={()=>setShowParagraph(!showParagraph)}>click me</Button>
    </div>
  );
}

export default App;
